    const handleChangeAdd = ()=>{
        setJobs([...jobs, newJob]);
    }
    return (
        <div>
           <label>
            Choose a job from this list:
            <input list="jobs" name={newJob} onChange={(e)=>{setNewJob(e.target.value)}}/>  
