import React, { useState } from 'react'
const mysql = require("mysql");
//database config

// const connection = mysql.createConnection({
//   host: "localhost",
//   user: "root",
//   password: "",
//   database: "jobs"
// });

// connection.connect(error => {
//   if (error) throw error;
//   console.log("Successfully connected to the database.");
// });
function Jobs() {
    // const listJobs = mysql.query(`SELECT * FROM job_Id`);
    // console.log(listJobs);
    const [jobs, setJobs] = useState(["Job_1", "Job_2", "Job_3"]);
    const [newJob, setNewJob] = useState();
    const handleChangeAdd = ()=>{
        setJobs([...jobs, newJob]);
        console.log(jobs);
    }
    return (
        <div>
           <label>
            Choose a  from this list:
            <input list="browsers" name={newJob} onChange={(e)=>{setNewJob(e.target.value)}}/>  
            <button onClick= {handleChangeAdd}>Add</button>
           </label>   
            <datalist id="browsers">
                {jobs.map((job, key)=>{
                return  <option key = {key} value = {job} />;
                })}   
            </datalist>
        </div>
    )
}

export default Jobs
