import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import ReactHTMLDatalist from "react-html-datalist";


const Jobs = props => {
  const [detail, setDetail] = useState({ food_id: "" });

     const handleChange = (e)=> {
        setDetail({ [e.target.name]: e.target.value });
      };
  

  const [jobs, setJobs] = useState([
    { text: "Pizza", value: "1" },
    { text: "Burger", value: "2" },
    { text: "Sandwich", value: "3" },
    { text: "French Fries", value: "4" }
  ]);
  
  const handleChangeAdd =()=>{
   setJobs([...jobs, detail]);
   console.log(jobs);
  }

  return (
    <div className="App">
      <h1>React HTML Datalist</h1>

      <p>
        <b>Selected food id:</b> {detail.food_id || "(none)"}
      </p>

      <small>Search or select in the datalist: </small>

      <ReactHTMLDatalist
        name={"job_id"}
        onChange={handleChange}
        classNames={"classone classtwo"}
        options={jobs.map(item=>(
            "Text" : item.nam
        ))}
      />
      <button onClick = {handleChangeAdd}>Add</button>
    </div>
  );
};

export default Jobs;
